mapper.update:
UPDATE profesor
SET nombre=#{nombre}, apellidoPaterno=#{apellidoPaterno}, apellidoMaterno=#{apellidoMaterno}, 
fechaNacimiento=#{fechaNacimiento}, fechaContratacion=#{fechaContratacion}, idRol = #{idRol}
WHERE idProfesor = #{idProfesor}

mapper.select:
SELECT idProfesor, TO_BASE64(foto) AS 'fotoBase64' 
FROM profesor 
WHERE idProfesor = #{idProfesor}
